export default {
  props: {
    dialog: {
      type: Boolean,
      required: true
    }
  },
  data() {
    return {
      internalDialog: this.dialog
    };
  },
  watch: {
    dialog(val) {
      this.internalDialog = val;
    },
    internalDialog(val) {
      this.$emit('update:dialog', val);
      if (!val) {
        this.$emit('close');
      }
    }
  },
  methods: {
    close() {
      this.internalDialog = false;
    },
    confirm() {
      this.$emit('confirm');
      this.internalDialog = false;
    }
  }
};
