export default {
  props: {
    dialog: Boolean,
    dialogTitle: String,
    editedItem: Object,
    rules: Object
  },
  data() {
    return {
      menu2: false,
      internalEditedItem: {}
    };
  },
  watch: {
    editedItem: {
      handler(newValue) {
        this.internalEditedItem = {
          ...newValue
        };
      },
      immediate: true
    }
  },
  methods: {
    closeDialog() {
      this.$emit('close-dialog');
    },
    saveItem() {
      if (this.$refs.form.validate()) {
        this.$emit('save-item', this.internalEditedItem);
      }
    }
  }
};
