import moment from 'moment';
export default {
  props: {
    dialog: Boolean,
    dialogTitle: String,
    editedItem: Object,
    rules: Object,
    statusOptions: Array
  },
  data() {
    return {
      internalDialog: this.dialog
    };
  },
  methods: {
    closeDialog() {
      this.internalDialog = false;
      this.$emit('update:dialog', this.internalDialog);
    },
    saveItem() {
      this.$emit('save');
    }
  },
  watch: {
    dialog(newVal) {
      this.internalDialog = newVal;
    }
  }
};
