import "core-js/modules/es.array.push.js";
import MyApplications from '@/components/MyApplications.vue';
import MyAccounts from '@/components/MyAccounts.vue';
import OrderDetails from '@/components/OrderDetails.vue';
export default {
  data() {
    return {
      activeTab: 0,
      tabs: [{
        label: 'Заявки',
        component: MyApplications,
        path: '/application'
      }, {
        label: 'Счета',
        component: MyAccounts,
        path: '/accounts'
      }],
      selectedOrderId: null
    };
  },
  methods: {
    navigateToOrder(orderId) {
      const orderDetailsTabIndex = this.tabs.findIndex(tab => tab.label === 'Детали заявки');
      if (orderDetailsTabIndex === -1) {
        this.tabs.push({
          label: 'Детали заявки',
          component: OrderDetails
        });
        this.activeTab = this.tabs.length - 1; // Активируем новый таб
      } else {
        this.activeTab = orderDetailsTabIndex; // Если таб уже существует, просто активируем его
      }
      this.$router.push({
        path: `/order/${orderId}`
      });
    }
  },
  watch: {
    activeTab(newVal) {
      const selectedTab = this.tabs[newVal];
      if (selectedTab && selectedTab.path) {
        this.$router.push({
          path: selectedTab.path
        });
      }
    }
  }
};
