import Vue from 'vue';
import Vuex from 'vuex';
import axios from 'axios';
import moment from 'moment';

Vue.use(Vuex);

export default new Vuex.Store({
  state: {
    ordersList: [],
    meetingsList: [],
    currentOrder: null,
    menu2: false,
    dialog: false,
    dialogDelete: false,
    dialogTitle: "Создать заявку",
    headers: [
      { text: '№ Заявки', value: 'num' },
      { text: 'Продукт', value: 'stg' },
      { text: 'Дата', value: 'dadd' },
      { text: 'Клиент', value: 'client_name' },
      { text: 'Статус', value: 'state' },
      { text: 'Телефон', value: 'person_phone' },
      { text: 'Действия', value: 'actions', sortable: false }
    ],statusOptions: [
      'init',
      'error',
      'upload_docs',
      'process',
      'reupload_fls'
    ],
    editedIndex: -1,
    editedItem: {
      num: 0,
      stg: [],
      dadd: moment().format('YYYY-MM-DD HH:mm'),
      client_name: "",
      state: "init",
      person_phone: ""
    },
    defaultItem: {
      num: 0,
      stg: [],
      dadd: moment().format('YYYY-MM-DD HH:mm'),
      client_name: "",
      state: "init",
      person_phone: ""
    },
  },
  mutations: {
    setOrdersList(state, orders) {
      state.ordersList = orders.map(order => ({
        ...order,
        dadd: moment(order.dadd).isValid() ? moment(order.dadd).format('YYYY-MM-DD HH:mm') : 'Invalid date'
      }));
    },
    addNewItem(state, item) {
      console.log(item);
      state.ordersList.push(item);
    },
    setMeetingsList(state, meetings) {
      state.meetingsList = meetings;
    },
    setCurrentOrder(state, order) {
      state.currentOrder = order;
    }
  },
  actions: {
    fetchOrdersList({ commit }) {
      axios.get('https://my-json-server.typicode.com/plushevy/demo/list')
        .then(response => {
          commit('setOrdersList', response.data);
        })
        .catch(error => {
          console.error('Ошибка при загрузке списка заявок:', error);
        });
    },
    fetchMeetingsList({ commit }) {
      axios.get('https://my-json-server.typicode.com/plushevy/demo/meetings')
        .then(response => {
          commit('setMeetingsList', response.data);
        })
        .catch(error => {
          console.error('Ошибка при загрузке списка встреч:', error);
        });
    },
    fetchOrderDetails({ commit }, orderId) {
      axios.get(`https://my-json-server.typicode.com/plushevy/demo/orders/${orderId}`)
        .then(response => {
          commit('setCurrentOrder', response.data);
        })
        .catch(error => {
          console.error('Ошибка при загрузке данных заявки:', error);
        });
    }
  },
  getters: {
    getMenu2: state => state.menu2,
    getDialog: state => state.dialog,
    getDialogDelete: state => state.dialogDelete,
    getDialogTitle: state => state.dialogTitle,
    getHeaders: state => state.headers,
    getStatusOptions: state => state.statusOptions,
    getEditedIndex: state => state.editedIndex,
    getEditedItem: state => state.editedItem,
    getDefaultItem: state => state.defaultItem,
  }
});
