export default {
  props: {
    dialog: Boolean,
    dialogTitle: String,
    editedItem: Object,
    rules: Object,
    statusOptions: Array // Добавляем это свойство
  },
  data() {
    return {
      menu2: false,
      internalDialog: false,
      // Создаем внутреннее состояние для диалога
      internalEditedItem: {}
    };
  },
  watch: {
    dialog: {
      handler(newValue) {
        this.internalDialog = newValue; // Обновляем внутреннее состояние при изменении пропа
      },
      immediate: true
    },
    editedItem: {
      handler(newValue) {
        this.internalEditedItem = {
          ...newValue
        };
      },
      immediate: true
    }
  },
  methods: {
    closeDialog() {
      this.internalDialog = false;
      this.$emit('close-dialog');
    },
    saveItem() {
      if (this.$refs.form.validate()) {
        this.$emit('save-item', this.internalEditedItem);
      }
    }
  }
};
