import "core-js/modules/es.array.push.js";
import MyApplications from '@/components/MyApplications.vue';
import MyAccounts from '@/components/MyAccounts.vue';
import OrderDetails from '@/components/OrderDetails.vue';
export default {
  data() {
    return {
      activeTab: 0,
      tabs: [{
        label: 'Заявки',
        component: MyApplications,
        path: '/application'
      }, {
        label: 'Счета',
        component: MyAccounts,
        path: '/accounts'
      }]
    };
  },
  created() {
    this.updateActiveTabBasedOnRoute();
    this.$root.$on('update-tabs', this.updateTabs); // Слушаем событие обновления табов
  },
  destroyed() {
    this.$root.$off('update-tabs', this.updateTabs); // Отменяем прослушивание события при уничтожении компонента
  },
  methods: {
    navigateToOrder(item) {
      const existingTabIndex = this.tabs.findIndex(tab => tab.label.includes(item.num));
      if (existingTabIndex !== -1) {
        this.activeTab = existingTabIndex;
      } else {
        this.tabs.push({
          label: 'Заявка ' + item.num,
          component: OrderDetails,
          path: '/application/' + item.id
        });
        this.activeTab = this.tabs.length - 1;
        this.$router.push({
          path: this.tabs[this.activeTab].path
        });
      }
    },
    updateTabs() {
      const ordersTab = this.tabs.find(tab => tab.label === 'Заявки');
      if (ordersTab) {
        ordersTab.data = this.$store.state.ordersList;
      }
    },
    updateActiveTabBasedOnRoute() {
      const currentPath = this.$route.path;
      const tabIndex = this.tabs.findIndex(tab => currentPath === tab.path);
      if (tabIndex !== -1) {
        this.activeTab = tabIndex;
      } else {
        const match = currentPath.match(/^\/application\/(\d+)/);
        if (match) {
          const orderId = match[1];
          const order = this.$store.state.ordersList.find(order => order.id === orderId);
          if (order) {
            const label = 'Заявка ' + order.num;
            const existingOrderTabIndex = this.tabs.findIndex(tab => tab.label === label);
            if (existingOrderTabIndex === -1) {
              this.tabs.push({
                label: label,
                component: OrderDetails,
                path: currentPath
              });
              this.activeTab = this.tabs.length - 1;
            } else {
              this.activeTab = existingOrderTabIndex;
            }
          } else {
            this.fetchOrderDetails(orderId).then(order => {
              const label = 'Заявка ' + order.num;
              this.tabs.push({
                label: label,
                component: OrderDetails,
                path: currentPath
              });
              this.activeTab = this.tabs.length - 1;
            });
          }
        }
      }
    },
    fetchOrderDetails(orderId) {
      return axios.get(`https://my-json-server.typicode.com/plushevy/demo/orders/${orderId}`).then(response => response.data).catch(error => {
        console.error('Ошибка при загрузке данных заявки:', error);
        return null;
      });
    }
  },
  watch: {
    activeTab(newVal) {
      const selectedTab = this.tabs[newVal];
      if (selectedTab && selectedTab.path && this.$route.path !== selectedTab.path) {
        this.$router.push({
          path: selectedTab.path
        });
      }
    }
  },
  beforeRouteUpdate(to, from, next) {
    this.updateActiveTabBasedOnRoute();
    next();
  }
};
