import "core-js/modules/es.array.push.js";
import MyApplications from '@/components/MyApplications.vue';
import MyAccounts from '@/components/MyAccounts.vue';
import OrderDetails from '@/components/OrderDetails.vue';
export default {
  data() {
    return {
      activeTab: 0,
      tabs: [{
        label: 'Заявки',
        component: MyApplications,
        path: '/application'
      }, {
        label: 'Счета',
        component: MyAccounts,
        path: '/accounts'
      }]
    };
  },
  methods: {
    navigateToOrder(item) {
      const existingTabIndex = this.tabs.findIndex(tab => tab.label.includes(item.num));
      if (existingTabIndex !== -1) {
        this.activeTab = existingTabIndex;
      } else {
        const newTabLabel = 'Детали заявки ' + item.num;
        const newTabKey = newTabLabel + '-' + item.orderId; // Adding orderId to ensure uniqueness
        this.tabs.push({
          label: newTabLabel,
          component: OrderDetails,
          path: '/application/' + item.orderId,
          key: newTabKey
        });
        this.activeTab = this.tabs.length - 1;
        if (this.$route.path !== '/application') {
          this.$router.push({
            path: '/application',
            replace: true
          });
        }
      }
    }
  },
  beforeRouteUpdate(to, from, next) {
    if (to.path.startsWith('/application/')) {
      next({
        path: '/application'
      });
    } else {
      next();
    }
  },
  created() {
    const selectedTab = this.tabs.find(tab => tab.path === this.$route.path);
    if (selectedTab) {
      this.activeTab = this.tabs.indexOf(selectedTab);
    }
  },
  watch: {
    activeTab(newVal) {
      const selectedTab = this.tabs[newVal];
      if (selectedTab && selectedTab.path) {
        this.$router.push({
          path: selectedTab.path
        });
      }
    }
  }
};
